import React,{ Component } from 'react';
import  {CardList}  from './Componet/card-list/card-list.component'; 
import {SearchBox} from './Componet/search-box/search-box.component';
import './App.css';

class  App extends Component {
  constructor(){
    super();
    this.state ={
      string:"Hello,This is first starting point of react js.",
      monsters:[],
      searchfield :""
    };
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(response => response.json())
    .then(users => this.setState({monsters:users}));
  } 

  searchUpdate = (e) => {
    this.setState({searchfield : e.target.value})
  }

  render(){
    const {monsters,searchfield} = this.state;
    const filteredmonsters = monsters.filter(monster => monster.name.toLowerCase().includes(searchfield.toLowerCase()))
    return(
      <div className="App">
        <h1>Monsters Rolodex</h1>
        <SearchBox  placeholder ="search cats"
        handleChange = {this.searchUpdate}
        />
        <CardList monsters = {filteredmonsters} />

      </div>
    )
  }

}

export default App;
